&lt;div id="quarto-navigation-envelope" class="hidden"&gt;
&lt;p&gt;&lt;span class="hidden" data-render-id="quarto-int-sidebar-title"&gt;Business French&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-navbar-title"&gt;Business French&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-next"&gt;&lt;span class="chapter-number"&gt;16&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.6 - Activité de groupe – phrases à corriger&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-prev"&gt;&lt;span class="chapter-number"&gt;14&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.4 - Qu’en disent leurs fondateurs?&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/index.html"&gt;&lt;span class="chapter-number"&gt;1&lt;/span&gt;  &lt;span class="chapter-title"&gt;À propos de ce cours&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/syllabus.html"&gt;&lt;span class="chapter-number"&gt;2&lt;/span&gt;  &lt;span class="chapter-title"&gt;Français des affaires&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-1"&gt;Module 1&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.1-intro.html"&gt;&lt;span class="chapter-number"&gt;3&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.1 - Intro&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.2.html"&gt;&lt;span class="chapter-number"&gt;4&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.2 - Étiquette de nom, extra longue&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.3.html"&gt;&lt;span class="chapter-number"&gt;5&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.3 L’entreprise, c’est quoi?&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.3.1.html"&gt;&lt;span class="chapter-number"&gt;6&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.3.1 - Petit lexique de l’économie&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.4.html"&gt;&lt;span class="chapter-number"&gt;7&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.4 - Quiz de compréhension – Désindustrialisation&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.5.html"&gt;&lt;span class="chapter-number"&gt;8&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.5 - Quiz de compréhension – Discours Macron Station F&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.6.html"&gt;&lt;span class="chapter-number"&gt;9&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.6 - Presse économique et des affaires&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod1/1.7.html"&gt;&lt;span class="chapter-number"&gt;10&lt;/span&gt;  &lt;span class="chapter-title"&gt;1.7 - Résumé du discours Macron&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-2"&gt;Module 2&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.1-objectifs.html"&gt;&lt;span class="chapter-number"&gt;11&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.1 - Objectifs&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.2.html"&gt;&lt;span class="chapter-number"&gt;12&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.2 - 4 jeunes entreprises à découvrir&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.3.html"&gt;&lt;span class="chapter-number"&gt;13&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.3 - Quiz de compréhension - 4 jeunes entreprises&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.4.html"&gt;&lt;span class="chapter-number"&gt;14&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.4 - Qu’en disent leurs fondateurs?&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.5.html"&gt;&lt;span class="chapter-number"&gt;15&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.5 - Présentation du Canevas d’opportunité et son application aux entreprises étudiées&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod2/2.6.html"&gt;&lt;span class="chapter-number"&gt;16&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.6 - Activité de groupe – phrases à corriger&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-3"&gt;Module 3&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.0-intro.html"&gt;&lt;span class="chapter-number"&gt;17&lt;/span&gt;  &lt;span class="chapter-title"&gt;Lettre de Non Motivation par Julien Prévieux&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.1.html"&gt;&lt;span class="chapter-number"&gt;18&lt;/span&gt;  &lt;span class="chapter-title"&gt;Profil créateur et dossier de candidature&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.2.html"&gt;&lt;span class="chapter-number"&gt;19&lt;/span&gt;  &lt;span class="chapter-title"&gt;Bilan de compétences&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.3.html"&gt;&lt;span class="chapter-number"&gt;20&lt;/span&gt;  &lt;span class="chapter-title"&gt;Speed-networking&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.4.html"&gt;&lt;span class="chapter-number"&gt;21&lt;/span&gt;  &lt;span class="chapter-title"&gt;Séance de réseautage rapide&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.5.html"&gt;&lt;span class="chapter-number"&gt;22&lt;/span&gt;  &lt;span class="chapter-title"&gt;Formation des groupes&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.6.html"&gt;&lt;span class="chapter-number"&gt;23&lt;/span&gt;  &lt;span class="chapter-title"&gt;Comparaison CV&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.7.html"&gt;&lt;span class="chapter-number"&gt;24&lt;/span&gt;  &lt;span class="chapter-title"&gt;Composition d’un CV&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.8.html"&gt;&lt;span class="chapter-number"&gt;25&lt;/span&gt;  &lt;span class="chapter-title"&gt;Activité accroche&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.9.html"&gt;&lt;span class="chapter-number"&gt;26&lt;/span&gt;  &lt;span class="chapter-title"&gt;Lettre de motivation&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.10.html"&gt;&lt;span class="chapter-number"&gt;27&lt;/span&gt;  &lt;span class="chapter-title"&gt;Phrases à corriger&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod3/3.11.html"&gt;&lt;span class="chapter-number"&gt;28&lt;/span&gt;  &lt;span class="chapter-title"&gt;Entretien d’embauche&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-4"&gt;Module 4&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.1.html"&gt;&lt;span class="chapter-number"&gt;29&lt;/span&gt;  &lt;span class="chapter-title"&gt;OptiMiam – Activités&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.2.html"&gt;&lt;span class="chapter-number"&gt;30&lt;/span&gt;  &lt;span class="chapter-title"&gt;Équipes efficaces et brainstorming&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.3.html"&gt;&lt;span class="chapter-number"&gt;31&lt;/span&gt;  &lt;span class="chapter-title"&gt;Brainstorming&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.4.html"&gt;&lt;span class="chapter-number"&gt;32&lt;/span&gt;  &lt;span class="chapter-title"&gt;Brainstorming II&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.5.html"&gt;&lt;span class="chapter-number"&gt;33&lt;/span&gt;  &lt;span class="chapter-title"&gt;Business Model&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.6.html"&gt;&lt;span class="chapter-number"&gt;34&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le Pitch&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.7.html"&gt;&lt;span class="chapter-number"&gt;35&lt;/span&gt;  &lt;span class="chapter-title"&gt;La phrase-projet&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.8.html"&gt;&lt;span class="chapter-number"&gt;36&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le pitch de la marque La Vie Laine&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.9.html"&gt;&lt;span class="chapter-number"&gt;37&lt;/span&gt;  &lt;span class="chapter-title"&gt;Comment trouver des idées d’entreprise? Les conseils des fondateurs de Onzic.&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.10.html"&gt;&lt;span class="chapter-number"&gt;38&lt;/span&gt;  &lt;span class="chapter-title"&gt;Présentation informelle des idées d’entreprise&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod4/4.11.html"&gt;&lt;span class="chapter-number"&gt;39&lt;/span&gt;  &lt;span class="chapter-title"&gt;Proposition d’une start-up&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-5"&gt;Module 5&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.1-intro.html"&gt;&lt;span class="chapter-number"&gt;40&lt;/span&gt;  &lt;span class="chapter-title"&gt;Introduction&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.2.html"&gt;&lt;span class="chapter-number"&gt;41&lt;/span&gt;  &lt;span class="chapter-title"&gt;Deux publicités&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.3.html"&gt;&lt;span class="chapter-number"&gt;42&lt;/span&gt;  &lt;span class="chapter-title"&gt;Les logos&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.4.html"&gt;&lt;span class="chapter-number"&gt;43&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le nom d’une marque&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.5.html"&gt;&lt;span class="chapter-number"&gt;44&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le nom et le logo d’une marque&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.6.html"&gt;&lt;span class="chapter-number"&gt;45&lt;/span&gt;  &lt;span class="chapter-title"&gt;Brainstorming: générer une grande quantité des noms&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.7.html"&gt;&lt;span class="chapter-number"&gt;46&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le slogan&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.8.html"&gt;&lt;span class="chapter-number"&gt;47&lt;/span&gt;  &lt;span class="chapter-title"&gt;Trouvez les slogans&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.9.html"&gt;&lt;span class="chapter-number"&gt;48&lt;/span&gt;  &lt;span class="chapter-title"&gt;Typologie des slogans&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.10.html"&gt;&lt;span class="chapter-number"&gt;49&lt;/span&gt;  &lt;span class="chapter-title"&gt;Classez les slogans&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.11.html"&gt;&lt;span class="chapter-number"&gt;50&lt;/span&gt;  &lt;span class="chapter-title"&gt;Création du slogan de votre marque&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod5/5.12.html"&gt;&lt;span class="chapter-number"&gt;51&lt;/span&gt;  &lt;span class="chapter-title"&gt;Le logo de votre entreprise&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:quarto-sidebar-section-6"&gt;Module 6&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod6/6.1.html"&gt;&lt;span class="chapter-number"&gt;52&lt;/span&gt;  &lt;span class="chapter-title"&gt;Exploration des sites web&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod6/6.2.html"&gt;&lt;span class="chapter-number"&gt;53&lt;/span&gt;  &lt;span class="chapter-title"&gt;Création de la page d’accueil de votre start-up&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-int-sidebar:/mod6/6.3.html"&gt;&lt;span class="chapter-number"&gt;54&lt;/span&gt;  &lt;span class="chapter-title"&gt;Refléxions finale&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-breadcrumbs-fae35184cc9121db497af2423562ef05"&gt;Module 2&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-breadcrumbs-359af9483cff5ff9776d81e9cd32b211"&gt;&lt;span class="chapter-number"&gt;15&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.5 - Présentation du Canevas d’opportunité et son application aux entreprises étudiées&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;
&lt;/div&gt;
&lt;div id="quarto-meta-markdown" class="hidden"&gt;
&lt;p&gt;&lt;span class="hidden" data-render-id="quarto-metatitle"&gt;Business French - &lt;span class="chapter-number"&gt;15&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.5 - Présentation du Canevas d’opportunité et son application aux entreprises étudiées&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-twittercardtitle"&gt;Business French - &lt;span class="chapter-number"&gt;15&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.5 - Présentation du Canevas d’opportunité et son application aux entreprises étudiées&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-ogcardtitle"&gt;Business French - &lt;span class="chapter-number"&gt;15&lt;/span&gt;  &lt;span class="chapter-title"&gt;2.5 - Présentation du Canevas d’opportunité et son application aux entreprises étudiées&lt;/span&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-metasitename"&gt;Business French&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-twittercarddesc"&gt;&lt;/span&gt; &lt;span class="hidden" data-render-id="quarto-ogcardddesc"&gt;&lt;/span&gt;&lt;/p&gt;
&lt;/div&gt;

&lt;/main&gt; &lt;!-- /main --&gt;
&lt;script id = "quarto-html-after-body" type="application/javascript"&gt;
window.document.addEventListener("DOMContentLoaded", function (event) {
  const toggleBodyColorMode = (bsSheetEl) =&gt; {
    const mode = bsSheetEl.getAttribute("data-mode");
    const bodyEl = window.document.querySelector("body");
    if (mode === "dark") {
      bodyEl.classList.add("quarto-dark");
      bodyEl.classList.remove("quarto-light");
    } else {
      bodyEl.classList.add("quarto-light");
      bodyEl.classList.remove("quarto-dark");
    }
  }
  const toggleBodyColorPrimary = () =&gt; {
    const bsSheetEl = window.document.querySelector("link#quarto-bootstrap");
    if (bsSheetEl) {
      toggleBodyColorMode(bsSheetEl);
    }
  }
  toggleBodyColorPrimary();  
  const icon = "";
  const anchorJS = new window.AnchorJS();
  anchorJS.options = {
    placement: 'right',
    icon: icon
  };
  anchorJS.add('.anchored');
  const isCodeAnnotation = (el) =&gt; {
    for (const clz of el.classList) {
      if (clz.startsWith('code-annotation-')) {                     
        return true;
      }
    }
    return false;
  }
  const clipboard = new window.ClipboardJS('.code-copy-button', {
    text: function(trigger) {
      const codeEl = trigger.previousElementSibling.cloneNode(true);
      for (const childEl of codeEl.children) {
        if (isCodeAnnotation(childEl)) {
          childEl.remove();
        }
      }
      return codeEl.innerText;
    }
  });
  clipboard.on('success', function(e) {
    // button target
    const button = e.trigger;
    // don't keep focus
    button.blur();
    // flash "checked"
    button.classList.add('code-copy-button-checked');
    var currentTitle = button.getAttribute("title");
    button.setAttribute("title", "Copied!");
    let tooltip;
    if (window.bootstrap) {
      button.setAttribute("data-bs-toggle", "tooltip");
      button.setAttribute("data-bs-placement", "left");
      button.setAttribute("data-bs-title", "Copied!");
      tooltip = new bootstrap.Tooltip(button, 
        { trigger: "manual", 
          customClass: "code-copy-button-tooltip",
          offset: [0, -8]});
      tooltip.show();    
    }
    setTimeout(function() {
      if (tooltip) {
        tooltip.hide();
        button.removeAttribute("data-bs-title");
        button.removeAttribute("data-bs-toggle");
        button.removeAttribute("data-bs-placement");
      }
      button.setAttribute("title", currentTitle);
      button.classList.remove('code-copy-button-checked');
    }, 1000);
    // clear code selection
    e.clearSelection();
  });
  function tippyHover(el, contentFn) {
    const config = {
      allowHTML: true,
      content: contentFn,
      maxWidth: 500,
      delay: 100,
      arrow: false,
      appendTo: function(el) {
          return el.parentElement;
      },
      interactive: true,
      interactiveBorder: 10,
      theme: 'quarto',
      placement: 'bottom-start'
    };
    window.tippy(el, config); 
  }
  const noterefs = window.document.querySelectorAll('a[role="doc-noteref"]');
  for (var i=0; i&lt;noterefs.length; i++) {
    const ref = noterefs[i];
    tippyHover(ref, function() {
      // use id or data attribute instead here
      let href = ref.getAttribute('data-footnote-href') || ref.getAttribute('href');
      try { href = new URL(href).hash; } catch {}
      const id = href.replace(/^#\/?/, "");
      const note = window.document.getElementById(id);
      return note.innerHTML;
    });
  }
      let selectedAnnoteEl;
      const selectorForAnnotation = ( cell, annotation) =&gt; {
        let cellAttr = 'data-code-cell="' + cell + '"';
        let lineAttr = 'data-code-annotation="' +  annotation + '"';
        const selector = 'span[' + cellAttr + '][' + lineAttr + ']';
        return selector;
      }
      const selectCodeLines = (annoteEl) =&gt; {
        const doc = window.document;
        const targetCell = annoteEl.getAttribute("data-target-cell");
        const targetAnnotation = annoteEl.getAttribute("data-target-annotation");
        const annoteSpan = window.document.querySelector(selectorForAnnotation(targetCell, targetAnnotation));
        const lines = annoteSpan.getAttribute("data-code-lines").split(",");
        const lineIds = lines.map((line) =&gt; {
          return targetCell + "-" + line;
        })
        let top = null;
        let height = null;
        let parent = null;
        if (lineIds.length &gt; 0) {
            //compute the position of the single el (top and bottom and make a div)
            const el = window.document.getElementById(lineIds[0]);
            top = el.offsetTop;
            height = el.offsetHeight;
            parent = el.parentElement.parentElement;
          if (lineIds.length &gt; 1) {
            const lastEl = window.document.getElementById(lineIds[lineIds.length - 1]);
            const bottom = lastEl.offsetTop + lastEl.offsetHeight;
            height = bottom - top;
          }
          if (top !== null && height !== null && parent !== null) {
            // cook up a div (if necessary) and position it 
            let div = window.document.getElementById("code-annotation-line-highlight");
            if (div === null) {
              div = window.document.createElement("div");
              div.setAttribute("id", "code-annotation-line-highlight");
              div.style.position = 'absolute';
              parent.appendChild(div);
            }
            div.style.top = top - 2 + "px";
            div.style.height = height + 4 + "px";
            let gutterDiv = window.document.getElementById("code-annotation-line-highlight-gutter");
            if (gutterDiv === null) {
              gutterDiv = window.document.createElement("div");
              gutterDiv.setAttribute("id", "code-annotation-line-highlight-gutter");
              gutterDiv.style.position = 'absolute';
              const codeCell = window.document.getElementById(targetCell);
              const gutter = codeCell.querySelector('.code-annotation-gutter');
              gutter.appendChild(gutterDiv);
            }
            gutterDiv.style.top = top - 2 + "px";
            gutterDiv.style.height = height + 4 + "px";
          }
          selectedAnnoteEl = annoteEl;
        }
      };
      const unselectCodeLines = () =&gt; {
        const elementsIds = ["code-annotation-line-highlight", "code-annotation-line-highlight-gutter"];
        elementsIds.forEach((elId) =&gt; {
          const div = window.document.getElementById(elId);
          if (div) {
            div.remove();
          }
        });
        selectedAnnoteEl = undefined;
      };
      // Attach click handler to the DT
      const annoteDls = window.document.querySelectorAll('dt[data-target-cell]');
      for (const annoteDlNode of annoteDls) {
        annoteDlNode.addEventListener('click', (event) =&gt; {
          const clickedEl = event.target;
          if (clickedEl !== selectedAnnoteEl) {
            unselectCodeLines();
            const activeEl = window.document.querySelector('dt[data-target-cell].code-annotation-active');
            if (activeEl) {
              activeEl.classList.remove('code-annotation-active');
            }
            selectCodeLines(clickedEl);
            clickedEl.classList.add('code-annotation-active');
          } else {
            // Unselect the line
            unselectCodeLines();
            clickedEl.classList.remove('code-annotation-active');
          }
        });
      }
  const findCites = (el) =&gt; {
    const parentEl = el.parentElement;
    if (parentEl) {
      const cites = parentEl.dataset.cites;
      if (cites) {
        return {
          el,
          cites: cites.split(' ')
        };
      } else {
        return findCites(el.parentElement)
      }
    } else {
      return undefined;
    }
  };
  var bibliorefs = window.document.querySelectorAll('a[role="doc-biblioref"]');
  for (var i=0; i&lt;bibliorefs.length; i++) {
    const ref = bibliorefs[i];
    const citeInfo = findCites(ref);
    if (citeInfo) {
      tippyHover(citeInfo.el, function() {
        var popup = window.document.createElement('div');
        citeInfo.cites.forEach(function(cite) {
          var citeDiv = window.document.createElement('div');
          citeDiv.classList.add('hanging-indent');
          citeDiv.classList.add('csl-entry');
          var biblioDiv = window.document.getElementById('ref-' + cite);
          if (biblioDiv) {
            citeDiv.innerHTML = biblioDiv.innerHTML;
          }
          popup.appendChild(citeDiv);
        });
        return popup.innerHTML;
      });
    }
  }
});
